export default{LiveSelect:{attachDomEventHandlers(){this.el.querySelector("input[type=text]").onkeydown=t=>{t.code==="Enter"&&t.preventDefault(),this.pushEventTo(this.el,"keydown",{key:t.code})}},setInputValue(t){this.el.querySelector("input[type=text]").value=t},inputEvent(t){const e=t==="single"?"input.hidden":"select";this.el.querySelector(e).dispatchEvent(new Event("input",{bubbles:!0}))},mounted(){this.handleEvent("reset",({id:t})=>{this.el.id===t&&(this.setInputValue(null),this.inputEvent("single"))}),this.handleEvent("select",({id:t,selection:e,mode:n})=>{if(this.el.id===t)if(n==="single"){const[{label:i}]=e;this.setInputValue(i),this.inputEvent(n)}else this.setInputValue(null),this.inputEvent(n)}),this.attachDomEventHandlers()},updated(){this.attachDomEventHandlers()}}};
